create PROCEDURE [dbo].[SpDeletenPagosNomina] @empresa int,@año int,@periodoNomina int,@mes nchar,@Retorno int output  AS begin tran nPagosNomina delete nPagosNomina where año = @año and empresa = @empresa and mes = @mes and periodoNomina = @periodoNomina if (@@error = 0 ) begin set @Retorno = 0 commit tran nPagosNomina end else begin set @Retorno = 1 rollback tran nPagosNomina end