create PROCEDURE [dbo].[SpDeletenParametrosTipoCotizante] @empresa int,@tipoCotizante varchar(50),@subTipoCotizante varchar(50),@Retorno int output  AS begin tran nParametrosTipoCotizante delete nParametrosTipoCotizante where empresa = @empresa and subTipoCotizante = @subTipoCotizante and tipoCotizante = @tipoCotizante if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametrosTipoCotizante end else begin set @Retorno = 1 rollback tran nParametrosTipoCotizante end