CREATE PROCEDURE [dbo].[SpDeletenPeriodoDetalle] @empresa int,@año int,@mes int,@noPeriodo int,@Retorno int output  AS begin tran nPeriodoDetalle delete nPeriodoDetalle where año = @año and empresa = @empresa and mes = @mes and noPeriodo = @noPeriodo if (@@error = 0 ) begin set @Retorno = 0 commit tran nPeriodoDetalle end else begin set @Retorno = 1 rollback tran nPeriodoDetalle end