create PROCEDURE [dbo].[SpDeletenPlanoBanco] @empresa int,@banco varchar(50),@Retorno int output  AS begin tran nPlanoBanco delete nPlanoBanco where banco = @banco and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nPlanoBanco end else begin set @Retorno = 1 rollback tran nPlanoBanco end