CREATE PROCEDURE [dbo].[SpDeletenTipoConceptoDetalle] @empresa int,@tipoConcepto varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nTipoConceptoDetalle delete nTipoConceptoDetalle where concepto = @concepto and empresa = @empresa and tipoConcepto = @tipoConcepto if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoConceptoDetalle end else begin set @Retorno = 1 rollback tran nTipoConceptoDetalle end