CREATE PROCEDURE [dbo].[SpDeletenTipoNomina] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nTipoNomina delete nTipoNomina where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoNomina end else begin set @Retorno = 1 rollback tran nTipoNomina end