create PROCEDURE [dbo].[SpDeletenVacaciones] @periodoInicial date,@periodoFinal date,@empresa int,@empleado int,@registro int,@Retorno int output  AS begin tran nVacaciones delete nVacaciones where empleado = @empleado and empresa = @empresa and periodoFinal = @periodoFinal and periodoInicial = @periodoInicial and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nVacaciones end else begin set @Retorno = 1 rollback tran nVacaciones end