CREATE PROCEDURE SpDeletesysEntidadCampo @entidad varchar(250),@campo varchar(250),@Retorno int output  AS begin tran sysEntidadCampo delete sysEntidadCampo where campo = @campo and entidad = @entidad if (@@error = 0 ) begin set @Retorno = 0 commit tran sysEntidadCampo end else begin set @Retorno = 1 rollback tran sysEntidadCampo end