CREATE PROCEDURE SpInsertaaSecciones @empresa int,@activo bit,@hBrutas decimal,@finca varchar(50),@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran aSecciones insert aSecciones( empresa,activo,hBrutas,finca,codigo,descripcion ) select @empresa,@activo,@hBrutas,@finca,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran aSecciones end else begin set @Retorno = 1 rollback tran aSecciones end