CREATE PROCEDURE SpInsertabProcedenciaCorreos @empresa int,@procedencia varchar(50),@direccion varchar(250),@Retorno int output  AS begin tran bProcedenciaCorreos insert bProcedenciaCorreos( empresa,procedencia,direccion ) select @empresa,@procedencia,@direccion if (@@error = 0 ) begin set @Retorno = 0 commit tran bProcedenciaCorreos end else begin set @Retorno = 1 rollback tran bProcedenciaCorreos end