CREATE PROCEDURE SpInsertabVehiculo @empresa int,@pesoTara int,@fechaRegistro datetime,@activo bit,@codigo varchar(50),@descripcion varchar(550),@usuario varchar(50),@tipo nchar,@Retorno int output  AS begin tran bVehiculo insert bVehiculo( empresa,pesoTara,fechaRegistro,activo,codigo,descripcion,usuario,tipo ) select @empresa,@pesoTara,@fechaRegistro,@activo,@codigo,@descripcion,@usuario,@tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran bVehiculo end else begin set @Retorno = 1 rollback tran bVehiculo end