CREATE PROCEDURE SpInsertacGrupoCCosto @empresa int,@activo bit,@codigo varchar(5),@descripcion varchar(550),@Retorno int output  AS begin tran cGrupoCCosto insert cGrupoCCosto( empresa,activo,codigo,descripcion ) select @empresa,@activo,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran cGrupoCCosto end else begin set @Retorno = 1 rollback tran cGrupoCCosto end