CREATE PROCEDURE SpInsertagEmpresa @id int,@tercero int,@fechaRegistro datetime,@activo bit,@extractora bit,@razonSocial varchar(550),@nit char(15),@dv char(1),@Retorno int output  AS begin tran gEmpresa insert gEmpresa( id,tercero,fechaRegistro,activo,extractora,razonSocial,nit,dv ) select @id,@tercero,@fechaRegistro,@activo,@extractora,@razonSocial,@nit,@dv if (@@error = 0 ) begin set @Retorno = 0 commit tran gEmpresa end else begin set @Retorno = 1 rollback tran gEmpresa end