CREATE PROCEDURE [dbo].[SpInsertagFormaPago] @empresa int,@cheque bit,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran gFormaPago insert gFormaPago( empresa,cheque,codigo,descripcion ) select @empresa,@cheque,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran gFormaPago end else begin set @Retorno = 1 rollback tran gFormaPago end