CREATE PROCEDURE SpInsertagTipoDocumento @empresa int,@codigoTD int,@descripcion varchar(250),@codigo char(2),@descripcionCorta char(3),@Retorno int output  AS begin tran gTipoDocumento insert gTipoDocumento( empresa,codigoTD,descripcion,codigo,descripcionCorta ) select @empresa,@codigoTD,@descripcion,@codigo,@descripcionCorta if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoDocumento end else begin set @Retorno = 1 rollback tran gTipoDocumento end