CREATE PROCEDURE SpInsertaiBodegaTipoTransaccion @empresa int,@tipo varchar(50),@bodega varchar(5),@Retorno int output  AS begin tran iBodegaTipoTransaccion insert iBodegaTipoTransaccion( empresa,tipo,bodega ) select @empresa,@tipo,@bodega if (@@error = 0 ) begin set @Retorno = 0 commit tran iBodegaTipoTransaccion end else begin set @Retorno = 1 rollback tran iBodegaTipoTransaccion end