CREATE PROCEDURE SpInsertaiNivelDestino @empresa int,@activo bit,@codigo varchar(2),@descripcion varchar(250),@Retorno int output  AS begin tran iNivelDestino insert iNivelDestino( empresa,activo,codigo,descripcion ) select @empresa,@activo,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran iNivelDestino end else begin set @Retorno = 1 rollback tran iNivelDestino end