CREATE PROCEDURE [dbo].[SpInsertanConceptoRango] @empresa int,@registro int,@minimo money,@maximo money,@valor money,@por bit,@porcentaje decimal(18,3),@concepto varchar(50),@Retorno int output  AS begin tran nConceptoRango insert nConceptoRango( empresa,registro,minimo,maximo,valor,por,porcentaje,concepto ) select @empresa,@registro,@minimo,@maximo,@valor,@por,@porcentaje,@concepto if (@@error = 0 ) begin set @Retorno = 0 commit tran nConceptoRango end else begin set @Retorno = 1 rollback tran nConceptoRango end