CREATE PROCEDURE [dbo].[SpInsertanConceptosFijosDetalle] @empresa int,@año int,@mes int,@noPeriodo int,@centroCosto varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nConceptosFijosDetalle insert nConceptosFijosDetalle( empresa,año,mes,noPeriodo,centroCosto,concepto ) select @empresa,@año,@mes,@noPeriodo,@centroCosto,@concepto if (@@error = 0 ) begin set @Retorno = 0 commit tran nConceptosFijosDetalle end else begin set @Retorno = 1 rollback tran nConceptosFijosDetalle end