CREATE PROCEDURE [dbo].[SpInsertanDepartamento] @empresa int,@activo bit,@ccosto varchar(50),@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran nDepartamento insert nDepartamento( empresa,activo,ccosto,codigo,descripcion ) select @empresa,@activo,@ccosto,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran nDepartamento end else begin set @Retorno = 1 rollback tran nDepartamento end