CREATE PROCEDURE [dbo].[SpInsertanMotivoRetiro] @empresa int,@fechaRegistro datetime,@activo bit,@codigo varchar(50),@descripcion varchar(550),@observacion varchar(5550),@usuario varchar(50),@Retorno int output  AS begin tran nMotivoRetiro insert nMotivoRetiro( empresa,fechaRegistro,activo,codigo,descripcion,observacion,usuario ) select @empresa,@fechaRegistro,@activo,@codigo,@descripcion,@observacion,@usuario if (@@error = 0 ) begin set @Retorno = 0 commit tran nMotivoRetiro end else begin set @Retorno = 1 rollback tran nMotivoRetiro end