CREATE PROCEDURE [dbo].[SpInsertanParametroSeguridadSocial] @empresa int,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran nParametroSeguridadSocial insert nParametroSeguridadSocial( empresa,codigo,descripcion ) select @empresa,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametroSeguridadSocial end else begin set @Retorno = 1 rollback tran nParametroSeguridadSocial end