create PROCEDURE [dbo].[SpInsertanParametrosTipoCotizante] @empresa int,@salud bit,@pension bit,@fondoSolidaridad bit,@arp bit,@caja bit,@sena bit,@icbf bit,@tipoCotizante varchar(50),@subTipoCotizante varchar(50),@Retorno int output  AS begin tran nParametrosTipoCotizante insert nParametrosTipoCotizante( empresa,salud,pension,fondoSolidaridad,arp,caja,sena,icbf,tipoCotizante,subTipoCotizante ) select @empresa,@salud,@pension,@fondoSolidaridad,@arp,@caja,@sena,@icbf,@tipoCotizante,@subTipoCotizante if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametrosTipoCotizante end else begin set @Retorno = 1 rollback tran nParametrosTipoCotizante end