create PROCEDURE [dbo].[SpInsertanPlanoBancoDetalle] @empresa int,@registro int,@inicio int,@longitud int,@tipoCampo int,@mValorFijo bit,@banco varchar(50),@nombreCampo varchar(500),@valorFijo varchar(50),@Retorno int output  AS begin tran nPlanoBancoDetalle insert nPlanoBancoDetalle( empresa,registro,inicio,longitud,tipoCampo,mValorFijo,banco,nombreCampo,valorFijo ) select @empresa,@registro,@inicio,@longitud,@tipoCampo,@mValorFijo,@banco,@nombreCampo,@valorFijo if (@@error = 0 ) begin set @Retorno = 0 commit tran nPlanoBancoDetalle end else begin set @Retorno = 1 rollback tran nPlanoBancoDetalle end