CREATE PROCEDURE [dbo].[SpInsertanTipoConceptoDetalle] @empresa int,@tipoConcepto varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nTipoConceptoDetalle insert nTipoConceptoDetalle( empresa,tipoConcepto,concepto ) select @empresa,@tipoConcepto,@concepto if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoConceptoDetalle end else begin set @Retorno = 1 rollback tran nTipoConceptoDetalle end