create PROCEDURE [dbo].[SpInsertanTurno] @empresa int,@horaInicio int,@horas int,@activo bit,@codigo varchar(50),@descripcion varchar(250),@Retorno int output  AS begin tran nTurno insert nTurno( empresa,horaInicio,horas,activo,codigo,descripcion ) select @empresa,@horaInicio,@horas,@activo,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran nTurno end else begin set @Retorno = 1 rollback tran nTurno end