create PROCEDURE [dbo].[SpInsertanTurnoDepartamento] @empresa int,@activo bit,@turno varchar(50),@departamento varchar(50),@Retorno int output  AS begin tran nTurnoDepartamento insert nTurnoDepartamento( empresa,activo,turno,departamento ) select @empresa,@activo,@turno,@departamento if (@@error = 0 ) begin set @Retorno = 0 commit tran nTurnoDepartamento end else begin set @Retorno = 1 rollback tran nTurnoDepartamento end