CREATE PROCEDURE [dbo].[SpInsertapProductoMovimiento] @empresa int,@prioridad int,@orden int,@resultado bit,@almacena bit,@mCalcular bit,@mDecimal bit,@mInforme bit,@activo bit,@producto varchar(50),@movimiento varchar(50),@formula varchar(950),@modulo varchar(150),@expresion varchar(8000),@Retorno int output  AS begin tran pProductoMovimiento insert pProductoMovimiento( empresa,prioridad,orden,resultado,almacena,mCalcular,mDecimal,mInforme,activo,producto,movimiento,formula, modulo, expresion ) select @empresa,@prioridad,@orden,@resultado,@almacena,@mCalcular,@mDecimal,@mInforme,@activo,@producto,@movimiento,@formula,@modulo, @expresion if (@@error = 0 ) begin set @Retorno = 0 commit tran pProductoMovimiento end else begin set @Retorno = 1 rollback tran pProductoMovimiento end