CREATE PROCEDURE SpInsertasPerfilPermisos @activo bit,@perfil varchar(50),@sitio varchar(150),@menu varchar(150),@operacion varchar(50),@Retorno int output  AS begin tran sPerfilPermisos insert sPerfilPermisos( activo,perfil,sitio,menu,operacion ) select @activo,@perfil,@sitio,@menu,@operacion if (@@error = 0 ) begin set @Retorno = 0 commit tran sPerfilPermisos end else begin set @Retorno = 1 rollback tran sPerfilPermisos end