CREATE PROCEDURE SpActualizalRegistroTanque @empresa int,@cantidad int,@tipo varchar(50),@numero varchar(50),@bodega varchar(5),@Retorno int output  AS begin tran lRegistroTanque update lRegistroTanque set cantidad = @cantidad where empresa = @empresa and tipo = @tipo and numero = @numero and bodega = @bodega if (@@error = 0 ) begin set @Retorno = 0 commit tran lRegistroTanque end else begin set @Retorno = 1 rollback tran lRegistroTanque end