
CREATE PROCEDURE [dbo].[SpInsertanLiquidacionNominaDetalle] @empresa int,@año int,@mes int,@noPeriodo int,@empleado int,@signo int,@valor money,@saldo money,@cantidad decimal,@tipo varchar(50),@numero varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nLiquidacionNominaDetalle insert nLiquidacionNominaDetalle( empresa,año,mes,noPeriodo,empleado,signo,valor,saldo,cantidad,tipo,numero,concepto ) select @empresa,@año,@mes,@noPeriodo,@empleado,@signo,@valor,@saldo,@cantidad,@tipo,@numero,@concepto if (@@error = 0 ) begin set @Retorno = 0 commit tran nLiquidacionNominaDetalle end else begin set @Retorno = 1 rollback tran nLiquidacionNominaDetalle end