CREATE PROCEDURE [dbo].[SpActualizaaGrupoCaracteristica] @empresa int,@codigo int,@activo bit,@descripcion varchar(500),@Retorno int output  AS begin tran aGrupoCaracteristica update aGrupoCaracteristica set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran aGrupoCaracteristica end else begin set @Retorno = 1 rollback tran aGrupoCaracteristica end