CREATE PROCEDURE SpActualizaaLotesDetalle @empresa int,@linea int,@noPalma int,@izquierda bit,@lote varchar(50),@Retorno int output  AS begin tran aLotesDetalle update aLotesDetalle set noPalma = @noPalma,izquierda = @izquierda where empresa = @empresa and linea = @linea and lote = @lote if (@@error = 0 ) begin set @Retorno = 0 commit tran aLotesDetalle end else begin set @Retorno = 1 rollback tran aLotesDetalle end