CREATE PROCEDURE [dbo].[SpActualizabRegistroBascula] @empresa int,@item int,@racimos int,@sacos int,@fecha datetime,@fechaBruto datetime,@fechaTara datetime,@fechaNeto datetime,@fechaProceso datetime,@pesoBruto float,@pesoTara float,@pesoNeto float,@pesoSacos float,@analisisRegistrado bit,@vehiculoInterno bit,@tipo varchar(50),@numero varchar(50),@tiquete varchar(50),@remision varchar(50),@tipoVehiculo varchar(50),@vehiculo varchar(50),@remolque varchar(50),@procedencia varchar(50),@finca varchar(50),@usuario varchar(50),@bodega varchar(50),@urlTiquete varchar(250),@sellos varchar(250),@tipoDescargue varchar(50),@codigoConductor varchar(50),@nombreConductor varchar(200),@tercero varchar(50),@pesoDescuento float,@Retorno int output  AS begin tran bRegistroBascula update bRegistroBascula set item = @item,racimos = @racimos,sacos = @sacos,fecha = @fecha,fechaBruto = @fechaBruto,fechaTara = @fechaTara,fechaNeto = @fechaNeto,fechaProceso = @fechaProceso,pesoBruto = @pesoBruto,pesoTara = @pesoTara,pesoNeto = @pesoNeto,pesoSacos = @pesoSacos,analisisRegistrado = @analisisRegistrado,vehiculoInterno = @vehiculoInterno,tiquete = @tiquete,remision = @remision,tipoVehiculo = @tipoVehiculo,vehiculo = @vehiculo,remolque = @remolque,procedencia = @procedencia,finca = @finca,usuario = @usuario,bodega = @bodega,urlTiquete = @urlTiquete,sellos = @sellos,tipoDescargue = @tipoDescargue,codigoConductor = @codigoConductor,nombreConductor = @nombreConductor,tercero = @tercero, pesoDescuento=@pesoDescuento where empresa = @empresa and numero = @numero and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran bRegistroBascula end else begin set @Retorno = 1 rollback tran bRegistroBascula end