CREATE PROCEDURE SpActualizacClaseIR @empresa int,@codigo int,@activo bit,@retencion bit,@impuesto bit,@descripcion varchar(950),@sigla varchar(5),@Retorno int output  AS begin tran cClaseIR update cClaseIR set activo = @activo,retencion = @retencion,impuesto = @impuesto,descripcion = @descripcion,sigla = @sigla where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cClaseIR end else begin set @Retorno = 1 rollback tran cClaseIR end