CREATE PROCEDURE [dbo].[SpActualizacClaseParametroContaNomi] @empresa int,@porTercero bit,@porCuenta bit,@porCentroCosto bit,@activo bit,@codigo varchar(50),@descripcion varchar(500),@tipo varchar(50),@tipoDocumento varchar(50),@comprobante varchar(50),@cuentaPuente varchar(50),@cuentaCruce varchar(50),@ccostoMayor varchar(50),@ccosto varchar(50),@Retorno int output  AS begin tran cClaseParametroContaNomi update cClaseParametroContaNomi set porTercero = @porTercero,porCuenta = @porCuenta,porCentroCosto = @porCentroCosto,activo = @activo,descripcion = @descripcion,tipo = @tipo,tipoDocumento = @tipoDocumento,comprobante = @comprobante,cuentaPuente = @cuentaPuente,cuentaCruce = @cuentaCruce,ccostoMayor = @ccostoMayor,ccosto = @ccosto where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cClaseParametroContaNomi end else begin set @Retorno = 1 rollback tran cClaseParametroContaNomi end