CREATE PROCEDURE SpActualizacConceptoIR @empresa int,@clase int,@baseMinima money,@baseGravable float,@tasa float,@activo bit,@codigo varchar(50),@descripcion varchar(950),@calculo char(1),@Retorno int output  AS begin tran cConceptoIR update cConceptoIR set clase = @clase,baseMinima = @baseMinima,baseGravable = @baseGravable,tasa = @tasa,activo = @activo,descripcion = @descripcion,calculo = @calculo where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cConceptoIR end else begin set @Retorno = 1 rollback tran cConceptoIR end