CREATE PROCEDURE SpActualizagParametros @empresa int,@codigo varchar(50),@descripcion varchar(250),@valor varchar(999),@Retorno int output  AS begin tran gParametros update gParametros set descripcion = @descripcion,valor = @valor where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gParametros end else begin set @Retorno = 1 rollback tran gParametros end