CREATE PROCEDURE SpActualizalAnalisis @empresa int,@orden int,@produccion bit,@despacho bit,@informe bit,@control bit,@activo bit,@descuenta bit,@codigo varchar(10),@descripcion varchar(250),@uMedida varchar(50),@Retorno int output  AS begin tran lAnalisis update lAnalisis set orden = @orden,produccion = @produccion,despacho = @despacho,informe = @informe,control = @control,activo = @activo,descuenta = @descuenta,descripcion = @descripcion,uMedida = @uMedida where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran lAnalisis end else begin set @Retorno = 1 rollback tran lAnalisis end