CREATE PROCEDURE [dbo].[SpActualizanCargo] @empresa int,@salarioMaximo money,@activo bit,@codigo varchar(50),@descripcion varchar(550),@CNO varchar(50),@jefeInmediato varchar(50),@observacion varchar(5550),@Retorno int output  AS begin tran nCargo update nCargo set salarioMaximo = @salarioMaximo,activo = @activo,descripcion = @descripcion,CNO = @CNO,jefeInmediato = @jefeInmediato,observacion = @observacion where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nCargo end else begin set @Retorno = 1 rollback tran nCargo end