CREATE PROCEDURE [dbo].[SpActualizanLiquidacionNomina] @empresa int,@año int,@mes int,@noPeriodo int,@estado int,@fecha datetime,@fechaRegistro datetime,@anulado bit,@usuarioAnulado bit,@cerrado bit,@tipo varchar(50),@numero varchar(50),@usuario varchar(50),@Retorno int output  AS begin tran nLiquidacionNomina update nLiquidacionNomina set estado = @estado,fecha = @fecha,fechaRegistro = @fechaRegistro,anulado = @anulado,usuarioAnulado = @usuarioAnulado,cerrado = @cerrado,usuario = @usuario where año = @año and empresa = @empresa and mes = @mes and noPeriodo = @noPeriodo and numero = @numero and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran nLiquidacionNomina end else begin set @Retorno = 1 rollback tran nLiquidacionNomina end