create PROCEDURE [dbo].[SpActualizanPlanoBancoDetalle] @empresa int,@registro int,@inicio int,@longitud int,@tipoCampo int,@mValorFijo bit,@banco varchar(50),@nombreCampo varchar(500),@valorFijo varchar(50),@Retorno int output  AS begin tran nPlanoBancoDetalle update nPlanoBancoDetalle set inicio = @inicio,longitud = @longitud,tipoCampo = @tipoCampo,mValorFijo = @mValorFijo,nombreCampo = @nombreCampo,valorFijo = @valorFijo where banco = @banco and empresa = @empresa and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nPlanoBancoDetalle end else begin set @Retorno = 1 rollback tran nPlanoBancoDetalle end