CREATE PROCEDURE SpDeleteaGrupoNovedad @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran aGrupoNovedad delete aGrupoNovedad where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran aGrupoNovedad end else begin set @Retorno = 1 rollback tran aGrupoNovedad end