CREATE PROCEDURE [dbo].[SpDeletegFormaPago] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran gFormaPago delete gFormaPago where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gFormaPago end else begin set @Retorno = 1 rollback tran gFormaPago end