CREATE PROCEDURE SpDeletegTipoTransaccionCampo @empresa int,@tipoTransaccion varchar(50),@entidad varchar(250),@campo varchar(250),@Retorno int output  AS begin tran gTipoTransaccionCampo delete gTipoTransaccionCampo where campo = @campo and empresa = @empresa and entidad = @entidad and tipoTransaccion = @tipoTransaccion if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionCampo end else begin set @Retorno = 1 rollback tran gTipoTransaccionCampo end