CREATE PROCEDURE SpDeletegUnidadMedida @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran gUnidadMedida delete gUnidadMedida where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gUnidadMedida end else begin set @Retorno = 1 rollback tran gUnidadMedida end