CREATE PROCEDURE SpDeletelRegistroAnalisis @empresa int,@tipo varchar(50),@numero varchar(50),@analisis varchar(10),@Retorno int output  AS begin tran lRegistroAnalisis delete lRegistroAnalisis where analisis = @analisis and empresa = @empresa and numero = @numero and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran lRegistroAnalisis end else begin set @Retorno = 1 rollback tran lRegistroAnalisis end