CREATE PROCEDURE [dbo].[SpDeletenEntidadFondoPension] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nEntidadFondoPension delete nEntidadFondoPension where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadFondoPension end else begin set @Retorno = 1 rollback tran nEntidadFondoPension end