create PROCEDURE [dbo].[SpDeletenGrupoConcepto] @empresa int,@codigo char(5),@Retorno int output  AS begin tran nGrupoConcepto delete nGrupoConcepto where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nGrupoConcepto end else begin set @Retorno = 1 rollback tran nGrupoConcepto end