 create PROCEDURE [dbo].[SpDeletenSeguridadSocialPila] @empresa int,@año int,@mes int,@registro int,@Retorno int output  AS begin tran nSeguridadSocialPila delete nSeguridadSocialPila where año = @año and empresa = @empresa and mes = @mes and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nSeguridadSocialPila end else begin set @Retorno = 1 rollback tran nSeguridadSocialPila end